import React, { useState } from 'react';
import './Game.css';

function Game() {
    const allNumbers = Array.from({length: 10}, (_, index)=>index + 1 );

    function shufflerArray(array){
        for(let i = array.length - 1; i > 0; i--){
            let j = Math.floor(Math.random() * i );
            [array[i],array[j]] =  [array[j],array[i]]
        }
        return array;
    }
    const randomNumbers = shufflerArray(allNumbers).slice(0,8);
    const randomNumbersTwos = shufflerArray([...randomNumbers]);

    const finalArray = [...randomNumbers,...randomNumbersTwos]
    const bestArrays =  shufflerArray([...finalArray])
    console.log(bestArrays);

  return (
    <div className="App">
      <header className="App-header">
        <div className="container" >
            {bestArrays.map( (bestArray,index) =>(
                 <div className="image_container" key={index}>
                    <h2>{bestArray }</h2>
                 </div>
                )
            )}

        </div>
      </header>
    </div>
  );
}

export default Game;
