import React, { useState } from 'react';
import './Game.css';

function Game() {
    const allNumbers = Array.from({length: 10}, (_, index)=>index + 1 );

    function shufflerArray(array){
        for(let i = array.length - 1; i > 0; i--){
            let j = Math.floor(Math.random() * i );
            [array[i],array[j]] =  [array[j],array[i]]
        }
        return array;
    }
    const randomNumbers = shufflerArray(allNumbers).slice(0,8);
    const randomNumbersTwos = shufflerArray([...randomNumbers]);

    const finalArray = [...randomNumbers,...randomNumbersTwos]
    const bestArrays =  shufflerArray([...finalArray])
    // console.log(bestArrays);

    // const beforeStyle = {
    //     content: '',
    //     width: '200px',
    //     height: '200px',
    //     border: '1px black solid',
    //     backgroundColor: 'rgb(240, 150, 46)',
    //     position: 'relative',
    // };

    const[unCoverNumber, setunCoverNumber] = useState(false);
    const handleClick = (e)=>{
        const clickedElementId = e.target.id;
        setunCoverNumber(!unCoverNumber)
    }
    const beforeStyle = (unCoverNumber)=>{
        if(unCoverNumber){
            return{
                content: '',
                width: '200px',
                height: '200px',
                border: '1px black solid',
                backgroundColor: 'rgb(240, 150, 46)',
                position: 'relative',
        }
        }else{
            return{
                content: '',
                width: '200px',
                height: '200px',
                border: '1px black solid',
                backgroundColor: 'none',
                position: 'relative',
            }
        }
    }

  return (
    <div className="App">
      <header className="App-header">
        <div className="container" >
            {bestArrays.map( (bestArray,index) =>(
                <div onClick={handleClick} style={beforeStyle(unCoverNumber)} key={index}>
                    <div className="image_container"  >
                        <h2>{bestArray }</h2>
                    </div>
                 </div>
                )
            )}
        </div>
      </header>
    </div>
  );
}

export default Game;
