import React, { useState, useMemo} from 'react';
import './Game.css';

function Game() {
    // ----------------数字-------------------
        const allNumbers = useMemo(()=>Array.from({length: 10}, (_, index)=>index + 1 ), [])

        function shufflerArray(array){
            for(let i = array.length - 1; i > 0; i--){
                let j = Math.floor(Math.random() * i );
                [array[i],array[j]] =  [array[j],array[i]]
            }
            return array;
        }
        const randomNumbers = useMemo(() => shufflerArray(allNumbers).slice(0,8), [allNumbers]);
        const randomNumbersTwos = useMemo(() => shufflerArray([...randomNumbers]), [randomNumbers]);

        const finalArray = useMemo(() => [...randomNumbers,...randomNumbersTwos], [randomNumbers, randomNumbersTwos]);
        const bestArrays =  useMemo(() => shufflerArray([...finalArray]), [finalArray]);


    // ----------------点击变色-------------------
        const[unCoverNumbers, setUnCoverNumbers] = useState(Array(bestArrays.length).fill(false));
        const[lastClick, setLastClick] = useState(null);
        const[lastIndex, setLastIndex] = useState(null);
        const[h2TextValue, setH2TextValue] = useState(null);

        const handleClick = (index, event)=>{
            const newUnCoverNumbers = [...unCoverNumbers];
            newUnCoverNumbers[index] = !newUnCoverNumbers[index];
            setUnCoverNumbers(newUnCoverNumbers);

            const parentElement = event.target.parentElement;
            const h2Div = parentElement.querySelector('div');
            const h2Child = h2Div.querySelector('h2');
            // const parentElementIndex = parentElement.getAttribute('key');
            // const h2Text = h2Child.innerText;
            // console.log(h2Text)
            setH2TextValue(h2Child.innerText)
            if(h2Child){
                // const h2Text = h2Child.innerText;

                const parentElementIndex = parentElement.getAttribute('data-key');
                console.log(h2TextValue)
                if((lastClick && lastClick.h2TextValue=== h2TextValue) && (lastIndex && lastIndex.parentElementIndex !== parentElementIndex) ){
                    h2Div.style.display = 'none';
                    lastClick.h2Div.style.display = 'none';
                    setLastClick(null);
                    setLastIndex(null);
                    setH2TextValue(null);
                }else{
                    setLastClick({h2TextValue,h2Div});
                    setLastIndex({parentElementIndex});
                }
            }
        }
        const change_cover_color = (isUncovered) => {
            if(isUncovered){
                return{
                    backgroundColor: 'transparent',
                }
            }else{
                return{
                    backgroundColor: 'rgb(240, 150, 46)',
                }
            }
        }

    // ----------------相同数字会消失-------------------

    // 点击后如何不是相同的数字，颜色会再次盖住数字  
    // 点击后如何是相同的数字，相同的两个数字会消失


  return (
    <div className="App">
      <header className="App-header">
        <div className="container" >
            {bestArrays.map( (bestArray,index) =>(
                <div className='cellContainer' key={index} data-key={index} onClick={(event) => handleClick(index,event)}>
                    <div className="number_container"  >
                        <h2>{bestArray}</h2>
                    </div>
                    <div className="color_cover_container" style={change_cover_color(unCoverNumbers[index])}></div>
                    {/* <div className="color_cover_container"></div> */}
                </div>
            )
            )}
        </div>
      </header>
    </div>
  );
}

export default Game;
