import React, { useState} from 'react';
import './Game.css';

function Game() {
    // ----------------数字-------------------
    const allNumbers = Array.from({length: 10}, (_, index)=>index + 1 );

    function shufflerArray(array){
        for(let i = array.length - 1; i > 0; i--){
            let j = Math.floor(Math.random() * i );
            [array[i],array[j]] =  [array[j],array[i]]
        }
        return array;
    }
    const randomNumbers = shufflerArray(allNumbers).slice(0,8);
    const randomNumbersTwos = shufflerArray([...randomNumbers]);

    const finalArray = [...randomNumbers,...randomNumbersTwos]
    const bestArrays =  shufflerArray([...finalArray])

    // ----------------点击变色-------------------
    const[unCoverNumbers, setUnCoverNumbers] = useState(Array(bestArrays.length).fill(false));
    const handleClick = (index)=>{
        const newUnCoverNumbers = [...unCoverNumbers];
        newUnCoverNumbers[index] = !newUnCoverNumbers[index];
        setUnCoverNumbers(newUnCoverNumbers);
    }
    const change_cover_color = (isUncovered) => {
        if(isUncovered){
            return{
                backgroundColor: 'transparent',
            }
        }else{
            return{
                backgroundColor: 'rgb(240, 150, 46)',
            }
        }
    }


  return (
    <div className="App">
      <header className="App-header">
        <div className="container" >
            {bestArrays.map( (bestArray,index) =>(
                <div className='cellContainer' key={index} onClick={() => handleClick(index)}>
                    <div className="number_container"  >
                        <h2>{bestArray}</h2>
                    </div>
                    <div className="color_cover_container" style={change_cover_color(unCoverNumbers[index])}></div>
                </div>
            )
            )}
        </div>
      </header>
    </div>
  );
}

export default Game;
